$(document).ready(function () {
   $(".hero").slick({
      dots: false,
      arrows: true,
      autoplay: true,
      autoplaySpeed: 1000, // Adjust the autoplay speed as needed
      infinite: true,
      speed: 300,
      slidesToShow: 1,
      adaptiveHeight: true,
   });

   $(".prev-hero").click(function() {
      $(".hero").slick('slickPrev');
   });

   $(".next-hero").click(function() {
      $(".hero").slick('slickNext');
   });

   $(".hero-mobile").slick({
      dots: false,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 1000, // Adjust the autoplay speed as needed
      infinite: true,
      speed: 300,
      slidesToShow: 1,
      adaptiveHeight: true,
   });

   $(".prev-hero-mobile").click(function() {
      $(".hero-mobile").slick('slickPrev');
   });

   $(".next-hero-mobile").click(function() {
      $(".hero-mobile").slick('slickNext');
   });

   $(".standards__container").slick({
      dots: false,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 1000, // Adjust the autoplay speed as needed
      infinite: true, // Enable infinite loop
      speed: 300,
      slidesToShow: 4,
      slidesToScroll: 1,
      adaptiveHeight: true,
      responsive: [
         {
            breakpoint: 1024,
            settings: {
               slidesToShow: 3,
               slidesToScroll: 1,
            },
         },
         {
            breakpoint: 600,
            settings: {
               slidesToShow: 2,
               slidesToScroll: 1,
            },
         },
         {
            breakpoint: 450,
            settings: {
               slidesToShow: 1,
               slidesToScroll: 1,
            },
         },
      ],
   });

   $(".prev-standards").click(function() {
      $(".standards__container").slick('slickPrev');
   });

   $(".next-standards").click(function() {
      $(".standards__container").slick('slickNext');
   });

   $(".success__carousels").slick({
      dots: false,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 1000, // Adjust the autoplay speed as needed
      infinite: true,
      speed: 3000,
      slidesToShow: 1,
      adaptiveHeight: true,
   });

   $(".prev-success").click(function() {
      $(".success__carousels").slick('slickPrev');
   });

   $(".next-success").click(function() {
      $(".success__carousels").slick('slickNext');
   });

   $(".insights__container").slick({
      dots: false,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 1000, // Adjust the autoplay speed as needed
      infinite: true,
      speed: 3000,
      slidesToShow: 1,
      adaptiveHeight: true,
   });

   $(".prev-insights").click(function() {
      $(".insights__container").slick('slickPrev');
   });

   $(".next-insights").click(function() {
      $(".insights__container").slick('slickNext');
   });

   $(".carousel__container").slick({
      dots: false,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 1000, // Adjust the autoplay speed as needed
      infinite: true,
      speed: 300,
      slidesToShow: 1,
      adaptiveHeight: true,
   });

   $(".prev-carousel").click(function() {
      $(".carousel__container").slick('slickPrev');
   });

   $(".next-carousel").click(function() {
      $(".carousel__container").slick('slickNext');
   });
});
